package com.sprintqa.java.class002;

public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num1 = 990;
		
		int result = num1%2; 
		
		if (result == 0) {
			System.out.print("number is even");
		} else {
			System.out.print("number is odd");
		}

	}

}
