package com.sprintqa.java.class009;

public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1; i<=5; i++) { //outer for-loop
			
			for(int j=1; j<=i; j++) { // inner for-loop
				
				
				System.out.print("*");
				
			} //end of inner loop
			System.out.println();
			
		} // end of outer loop
	
	
	
	
	}

}